document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector(".info_open-btn"),t=document.querySelector(".more_info-title"),o=document.querySelector(".more_info-content");t.addEventListener("click",()=>{o.classList.toggle("active_content"),e.classList.toggle("close_panel"),o.classList.contains("active_content")?o.style.maxHeight=0:o.style.maxHeight=o.scrollHeight+"px"});const c=document.querySelector(".scroll_top");window.addEventListener("scroll",()=>{window.scrollY>100?c.classList.remove("scroll_hide"):c.classList.add("scroll_hide")}),c.addEventListener("click",()=>{window.scrollTo(0,0)})});